mapper.select:
	select * 
	from(
			SELECT rownum rn, a2.*
			FROM
	 	  	(
	 	  		Select rownum rn2, a.*
	 	  		From	
	 	  			(select * from oconsult order by ogroup asc, ostep desc) a
       					order by rn2 desc
	 	  	) a2
      	)
    WHERE rn between #{start} and #{end}
    order by rn

mapper.update:
update oconsult 
set	   otitle =#{otitle}, owriter =#{owriter},
	   ocontent =#{ocontent} 
where  onum=#{onum}

update 	oconsult 
set 	ocount=ocount+1 
Where	onum = #{onum}
